INSERT INTO sv_file 
(bno
, file_order
, filename
, f_path
, file_date
)
VALUES
(#{bno}
, sv_f_file_order_SEQUENCE.nextval
, #{filename}
, #{f_path}
, #{file_date}
)

mapper.delete:
DELETE
FROM sv_file 
WHERE bno = #{bno}
AND file_order = #{file_order}

mapper.update:
UPDATE sv_board
SET title = #{title},
description = #{description}
WHERE bno = #{bno}

UPDATE sv_board
SET view_no = #{view_no}
WHERE bno = #{bno}

