mapper.select:
SELECT bno 
FROM sv_board 
WHERE sv_type = #{sv_type}

SELECT count(*)
FROM sv_board 
WHERE sv_type = #{sv_type}

SELECT bno 
FROM sv_board 
WHERE sv_type = #{sv_type}
AND group_origin = #{group_origin}

mapper.insert:
INSERT INTO sv_board 
(bno
, sv_type
, group_origin
, group_order
, admin_type
, nickname
, title
, description
, create_time
, heart
, view_no
, member_no
)
VALUES
(#{bno}
, #{sv_type}
, 1
, 1
, #{admin_type}
, #{nickname}
, #{title}
, #{description}
, #{create_time}
, 0
, 0
, #{member_no}
)

INSERT INTO sv_file 
(bno
, file_order
, filename
, f_path
, file_date
)
VALUES
(#{bno}
, sv_f_file_order_SEQUENCE.nextval
, #{filename}
, #{f_path}
, #{file_date}
)

mapper.delete:
DELETE
FROM sv_file 
WHERE bno = #{bno}
AND file_order = #{file_order}

mapper.update:
UPDATE sv_board
SET title = #{title},
description = #{description}
WHERE bno = #{bno}

UPDATE sv_board
SET view_no = #{view_no}
WHERE bno = #{bno}

mapper.select:
SELECT count(*) 
FROM sv_viewcheck 
WHERE bno = #{bno}
AND member_no = #{member_no}

SELECT count(*) 
FROM sv_viewcheck 
WHERE bno = #{bno}
AND member_ip = #{member_ip}

mapper.insert:
INSERT INTO sv_viewcheck 
(bno
, view_time
, member_ip
, member_no
)
VALUES
(#{bno}
, to_char(sysdate, 'yyyymmdd')
, 'none'
, #{member_no}
)

INSERT INTO sv_viewcheck 
(bno
, view_time
, member_ip
, member_no
)
VALUES
(#{bno}
, to_char(sysdate, 'yyyymmdd')
, #{member_ip}
, 0
)

mapper.select:
SELECT * 
FROM sv_viewcheck  
WHERE bno = #{bno}
AND member_no = #{member_no}

SELECT * 
FROM sv_viewcheck  
WHERE bno = #{bno}
AND member_ip = #{member_ip}

mapper.update:
UPDATE sv_viewcheck
SET view_time = to_char(sysdate, 'yyyymmdd')
WHERE bno = #{bno}
AND member_no = #{member_no}

UPDATE sv_viewcheck
SET view_time = to_char(sysdate, 'yyyymmdd')
WHERE bno = #{bno}
AND member_ip = #{member_ip}

mapper.insert:
INSERT INTO sv_likecheck 
(bno
, like_check
, dislike_check
, member_no
)
VALUES
(#{bno}
, 1
, 0
, #{member_no}
)

mapper.update:
UPDATE sv_likecheck
SET like_check = 1
WHERE bno = #{bno}
AND member_no = #{member_no}

UPDATE sv_likecheck
SET like_check = 0
WHERE bno = #{bno}
AND member_no = #{member_no}

UPDATE sv_board
SET heart = #{heart}
WHERE bno = #{bno}

mapper.select:
SELECT *
FROM sv_likecheck
WHERE bno = #{bno}
AND member_no = #{member_no}
